import WheelComponent from 'react-wheel-of-prizes'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';

import './App.css';

function App() {
  const segments = [
    'better luck next time',
    'won 70',
    'won 10',
    'better luck next time',
    'won 2',
    'won uber pass',
    'better luck next time',
    'won a voucher'
  ]
  const segColors = [
    '#EE4040',
    '#F0CF50',
    '#815CD1',
    '#3DA5E0',
    '#34A24F',
    '#F9AA1F',
    '#EC3F3F',
    '#FF9000'
  ]
  const onFinished = (winner) => {
    console.log(winner)
  }

  return (
    <Container>
      <Row className="justify-content-center">
        <WheelComponent
            segments={segments}
            segColors={segColors}
            winningSegment='won 10'
            onFinished={(winner) => onFinished(winner)}
            primaryColor='black'
            contrastColor='white'
            buttonText='Spin'
            isOnlyOnce={false}
            size={290}
            upDuration={1000}
            downDuration={10}
            fontFamily='Arial'
          />
      </Row>
    </Container>
  );
}

export default App;
