import WheelComponent from 'react-wheel-of-prizes'
import Container from 'react-bootstrap/Container';
import { Row, Col } from 'react-bootstrap';

import './App.css';

function App() {
  const segments = [
    'better luck next time',
    'won 70',
    'won 10',
    'better luck next time',
    'won 2',
    'won uber pass',
    'better luck next time',
    'won a voucher'
  ]
  const segColors = [
    '#EE4040',
    '#F0CF50',
    '#815CD1',
    '#3DA5E0',
    '#34A24F',
    '#F9AA1F',
    '#EC3F3F',
    '#FF9000'
  ]
  const onFinished = (winner) => {
    console.log(winner)
  }

  return (
    <Container className="p-5">
      <Row className="justify-content-center m-5 p-5">
        <Col sz={6}>
          <WheelComponent
              segments={segments}
              segColors={segColors}
              winningSegment='won 10'
              onFinished={(winner) => onFinished(winner)}
              primaryColor='black'
              contrastColor='white'
              buttonText='Spin'
              isOnlyOnce={false}
              size={290}
              upDuration={1000}
              downDuration={10}
              fontFamily='Arial'
            />
        </Col>

      </Row>
    </Container>
  );
}

export default App;
